import React from 'react';

const Recommendations = ({ recommendations , onProductSelect }) => {
  // Don't render anything if there's no recommendation object yet
  if (!recommendations) {
    return null;
  }

  // Handle the case where the API returned an error
  if (recommendations.error) {
    return <div className="recommendations-container error-message">Error: {recommendations.error}</div>;
  }

  // --- THIS IS THE NEW LOGIC ---
  // Check if the recommendations array is empty.
  if (!recommendations.recommendations || recommendations.recommendations.length === 0) {
    return (
      <div className="recommendations-container">
        <h2>AI Recommendations</h2>
        <p>No relevant products found for your request. Please try different preferences.</p>
      </div>
    );
  }

  // If we have recommendations, display them
  return (
    <div className="recommendations-container">
      <h2>AI Recommendations</h2>
      {recommendations.recommendations.map((rec, index) => (
        <div key={index} className="rec-card clickable" onClick={() => onProductSelect(rec.product_id)}>
          <h3>Product ID: {rec.product_id}</h3>
          <p>{rec.reason}</p>
        </div>
      ))}
    </div>
  );
};

export default Recommendations;
