import React, { useState } from 'react';
import Catalog from './components/Catalog';
import UserPreferences from './components/UserPreferences'; 
import BrowsingHistory from './components/BrowsingHistory';
import Recommendations from './components/Recommendations';
import './styles/App.css';

function App() {
  const [preferences, setPreferences] = useState({ category: '', price_range: '' });
  const [history, setHistory] = useState([]);
  const [recommendations, setRecommendations] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleProductClick = (product) => {
    if (!history.find(p => p.id === product.id)) {
      setHistory([...history, product]);
    }
  };

  const handleGetRecommendations = async () => {
    setIsLoading(true);
    setRecommendations(null); // Clear old recommendations
    try {
      const response = await fetch('http://127.0.0.1:5001/api/recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          preferences: preferences, 
          history: history.map(({ id, name, category }) => ({ id, name, category })) // Send a lighter version of history
        }),
      });
      const data = await response.json();
      setRecommendations(data);
    } catch (error) {
      console.error('Failed to fetch recommendations:', error);
      setRecommendations({ error: 'Failed to connect to the server.' });
    }
    setIsLoading(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Product Recommender</h1>
      </header>
      <main>
        <div className="main-layout">
          <div className="left-panel">
            <UserPreferences 
              preferences={preferences} 
              setPreferences={setPreferences} 
              onSubmit={handleGetRecommendations}
            />
            <BrowsingHistory history={history} />
            {isLoading && <div className="loading">Getting recommendations...</div>}
            <Recommendations recommendations={recommendations} />
          </div>
          <div className="right-panel">
            <Catalog onProductClick={handleProductClick} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
