import React, { useState, useEffect } from 'react'; // <-- useEffect is needed
import Catalog from './components/Catalog';
import UserPreferences from './components/UserPreferences'; 
import BrowsingHistory from './components/BrowsingHistory';
import Recommendations from './components/Recommendations';
import ProductDetail from './components/ProductDetail'; // <-- Import ProductDetail
import './styles/App.css';

function App() {
  const [preferences, setPreferences] = useState({ category: '', price_range: '' });
  const [history, setHistory] = useState([]);
  const [recommendations, setRecommendations] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // --- MISSING PIECES ---
  const [productCatalog, setProductCatalog] = useState([]); // NEW: State for all products
  const [selectedProduct, setSelectedProduct] = useState(null); // NEW: State for the selected product

  // NEW: Fetch all products when the component first loads
  useEffect(() => {
    const fetchAllProducts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5001/api/products');
        const data = await response.json();
        setProductCatalog(data); // Store all products in state
      } catch (error) {
        console.error("Failed to fetch the product catalog:", error);
      }
    };
    fetchAllProducts();
  }, []); // The empty array [] means this runs only once

  const handleProductClick = (product) => {
    if (!history.find(p => p.id === product.id)) {
      setHistory([...history, product]);
    }
    setSelectedProduct(product); // Show details when a product is clicked
  };

  const handleGetRecommendations = async () => {
    setIsLoading(true);
    setRecommendations(null);
    try {
      const response = await fetch('http://127.0.0.1:5001/api/recommendations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          preferences: preferences, 
          history: history.map(({ id, name, category }) => ({ id, name, category }))
        }),
      });
      const data = await response.json();
      setRecommendations(data);
    } catch (error) {
      console.error('Failed to fetch recommendations:', error);
      setRecommendations({ error: 'Failed to connect to the server.' });
    }
    setIsLoading(false);
  };

  // This function will now work because productCatalog and setSelectedProduct exist
  const handleRecommendationClick = (productId) => {
    const product = productCatalog.find(p => p.id === productId);
    setSelectedProduct(product);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Product Recommender</h1>
      </header>
      <main>
        <div className="main-layout">
          <div className="left-panel">
            <UserPreferences 
              preferences={preferences} 
              setPreferences={setPreferences} 
              onSubmit={handleGetRecommendations}
            />
            <BrowsingHistory history={history} />
            {isLoading && <div className="loading">Getting recommendations...</div>}
            <Recommendations
              recommendations={recommendations} 
              onProductSelect={handleRecommendationClick}
              productCatalog={productCatalog}
            />
          </div>
          <div className="right-panel">
            {/* NEW: Conditional logic to show either details or catalog */}
            {selectedProduct ? (
              <ProductDetail 
                product={selectedProduct} 
                onClose={() => setSelectedProduct(null)} 
              />
            ) : (
              <Catalog 
                products={productCatalog} 
                onProductClick={handleProductClick} 
              />
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
